function prDeleteGroups(){var a=jQuery(".pr_group_name:checked").map(function(b,c){return jQuery(c).attr("pr-group-name")});if(0==a.length){return false}jQuery.get(ajaxurl,{action:"pr_delete_groups",groups:a.toArray()},function(b){prUpdateActivityList()})}function prAddGroup(){var a=jQuery(".pr_activity_id:checked").map(function(c,d){return jQuery(d).attr("pr-activity-id")});var b=jQuery("#pr_group_name").val();if(0==a.length){alert("Please select activities first.");return}if(!b){alert("Please enter a group name first.");return}jQuery.get(ajaxurl,{action:"pr_store_group",idlist:a.toArray(),groupname:b},function(c){if(true==c.success){prUpdateActivityList()}else{console.log(c.message)}})}function prUpdateActivityList(){var a={action:"pr_activity_list"};jQuery.get(ajaxurl,a,function(b){jQuery("#prActivityTable").html(b)})}jQuery(document).ready(function(){prUpdateActivityList();jQuery(document).on("click","#pr_add_group",function(a){prAddGroup()});jQuery(document).on("click","#pr_refresh",function(a){prUpdateActivityList()});jQuery(document).on("click","#pr_delete_groups",function(a){prDeleteGroups()})});